mapper.select:
SELECT /* selectContentsHistory */ contents_history.contents_history_id , contents_history.online_subject_schedule_id, contents_history.end_date, contents_history.summary_doing_drawing_file_id , contents_history.summary_doing_voice_file_id, contents_history.user_manage_id,
DRAWING_COMTNFILE.ATCH_FILE_ID as DRAWING_ATCH_FILE_ID, DRAWING_COMTNFILEDETAIL.FILE_CN as DRAWING_FILE_CN, DRAWING_COMTNFILEDETAIL.FILE_SN as DRAWING_FILE_SN, DRAWING_COMTNFILEDETAIL.FILE_STRE_COURS as DRAWING_FILE_STRE_COURS, DRAWING_COMTNFILEDETAIL.STRE_FILE_NM as DRAWING_STRE_FILE_NM, DRAWING_COMTNFILEDETAIL.FILE_EXTSN as DRAWING_FILE_EXTSN, DRAWING_COMTNFILEDETAIL.ORIGNL_FILE_NM as DRAWING_ORIGNL_FILE_NM, DRAWING_COMTNFILEDETAIL.FILE_SIZE as DRAWING_FILE_SIZE, DRAWING_COMTNFILE.CREAT_DT as DRAWING_CREAT_DT, DRAWING_COMTNFILEDETAIL.DOWNLOAD_CNT as DRAWING_DOWNLOAD_CNT,
VOICE_COMTNFILE.ATCH_FILE_ID as VOICE_ATCH_FILE_ID, VOICE_COMTNFILEDETAIL.FILE_CN as VOICE_FILE_CN, VOICE_COMTNFILEDETAIL.FILE_SN as VOICE_FILE_SN, VOICE_COMTNFILEDETAIL.FILE_STRE_COURS as VOICE_FILE_STRE_COURS, VOICE_COMTNFILEDETAIL.STRE_FILE_NM as VOICE_STRE_FILE_NM, VOICE_COMTNFILEDETAIL.FILE_EXTSN as VOICE_FILE_EXTSN, VOICE_COMTNFILEDETAIL.ORIGNL_FILE_NM as VOICE_ORIGNL_FILE_NM, VOICE_COMTNFILEDETAIL.FILE_SIZE as VOICE_FILE_SIZE, VOICE_COMTNFILE.CREAT_DT as VOICE_CREAT_DT, VOICE_COMTNFILEDETAIL.DOWNLOAD_CNT as VOICE_DOWNLOAD_CNT from contents_history as contents_history LEFT join COMTNFILE as DRAWING_COMTNFILE on contents_history.summary_doing_drawing_file_id = DRAWING_COMTNFILE.ATCH_FILE_ID LEFT JOIN COMTNFILEDETAIL as DRAWING_COMTNFILEDETAIL on contents_history.summary_doing_drawing_file_id = DRAWING_COMTNFILEDETAIL.ATCH_FILE_ID LEFT join COMTNFILE as VOICE_COMTNFILE on contents_history.summary_doing_voice_file_id = VOICE_COMTNFILE.ATCH_FILE_ID LEFT JOIN COMTNFILEDETAIL as VOICE_COMTNFILEDETAIL on contents_history.summary_doing_voice_file_id = VOICE_COMTNFILEDETAIL.ATCH_FILE_ID where 1=1 and contents_history.user_manage_id = #{userManageId} and contents_history.online_subject_schedule_id = #{onlineSubjectScheduleId} and contents_history.delete_ornot = 'N';

select /* selectBook */ book.book_id, book.book_title, book.book_thumbnail_file_id,
labelManage.label_manage_id, labelManage.label_title,
COMTNFILE.ATCH_FILE_ID, COMTNFILEDETAIL.FILE_CN, COMTNFILEDETAIL.FILE_SN, COMTNFILEDETAIL.FILE_STRE_COURS, COMTNFILEDETAIL.STRE_FILE_NM, COMTNFILEDETAIL.FILE_EXTSN, COMTNFILEDETAIL.ORIGNL_FILE_NM, COMTNFILEDETAIL.FILE_SIZE, COMTNFILE.CREAT_DT, COMTNFILEDETAIL.DOWNLOAD_CNT FROM online_subject_schedule as schedule INNER JOIN contents as contents on schedule.contents_id = contents.contents_id INNER JOIN book as book on contents.book_id = book.book_id LEFT JOIN COMTNFILE on book.book_thumbnail_file_id = COMTNFILE.ATCH_FILE_ID LEFT JOIN COMTNFILEDETAIL on book.book_thumbnail_file_id = COMTNFILEDETAIL.ATCH_FILE_ID LEFT JOIN label_mapping labelMapping on labelMapping.label_domain_manage_id = 1 /* 1:책 */ and labelMapping.object_manage_id = book.book_id and labelMapping.delete_ornot = 'N' LEFT JOIN label_manage labelManage on labelManage.label_manage_id = labelMapping.label_manage_id and labelManage.label_domain_manage_id = labelMapping.label_domain_manage_id and labelManage.delete_ornot = 'N' where schedule.online_subject_schedule_id = #{onlineSubjectScheduleId}

SELECT /* selectDicList */ history.contents_history_id, book.book_id, book.book_title,
template_inst.template_inst_id, template_inst.json, template_inst.template_id, template.template_title
FROM contents_history as history left join template_inst_history as tempate_inst_history on history.contents_history_id = tempate_inst_history.contents_history_id LEFT JOIN online_subject_schedule as schedule on history.online_subject_schedule_id = schedule.online_subject_schedule_id left JOIN contents as contents on schedule.contents_id = contents.contents_id LEFT JOIN template_inst as template_inst on contents.contents_id = template_inst.contents_id and tempate_inst_history.template_inst_id = template_inst.template_inst_id and template_inst.delete_ornot='N' LEFT JOIN template as template on template_inst.template_id = template.template_id and template.delete_ornot = 'N' INNER JOIN book as book on contents.book_id = book.book_id where history.delete_ornot = 'N' and history.contents_history_id =#{contentsHistoryId}
