mapper.select:
SELECT /* selectPopup */ schedule.online_subject_schedule_id,
book.book_id, book.book_thumbnail_file_id,
COMTNFILE.ATCH_FILE_ID, COMTNFILEDETAIL.FILE_CN, COMTNFILEDETAIL.FILE_SN, COMTNFILEDETAIL.FILE_STRE_COURS, COMTNFILEDETAIL.STRE_FILE_NM, COMTNFILEDETAIL.FILE_EXTSN, COMTNFILEDETAIL.ORIGNL_FILE_NM, COMTNFILEDETAIL.FILE_SIZE, COMTNFILE.CREAT_DT, COMTNFILEDETAIL.DOWNLOAD_CNT FROM online_subject_schedule schedule INNER JOIN contents contents on schedule.contents_id = contents.contents_id INNER JOIN book book on contents.book_id = book.book_id LEFT JOIN COMTNFILE on book.book_thumbnail_file_id = COMTNFILE.ATCH_FILE_ID LEFT JOIN COMTNFILEDETAIL on book.book_thumbnail_file_id = COMTNFILEDETAIL.ATCH_FILE_ID WHERE 1=1 AND schedule.online_subject_schedule_id = #{onlineSubjectScheduleId}
